import React, { useState, useRef } from 'react';
import GoogleMapReact from "google-map-react";
import Polyline from 'react';

  const Location = () => {
  const [lat, setLat] = useState(null);
  const [lng, setLng] = useState(null);
  const [status, setStatus] = useState(null);
  const coordinates = [];
  const watchId = useRef(null);


  const getLocation = () => {
    if (!navigator.geolocation) {
      setStatus('Geolocation is not supported by your browser');
    } else {
      setStatus('Locating...');
      navigator.geolocation.watchPosition((position) => {
        setStatus(null);
        setLat(position.coords.latitude);
        setLng(position.coords.longitude);
        console.log("Latitude is :", position.coords.latitude);
        console.log("Longitude is :", position.coords.longitude);
        coordinates.push([position.coords.latitude, position.coords.longitude]);
        window.localStorage.setItem("coordinates", JSON.stringify(coordinates));

      }, () => {
        setStatus('Unable to retrieve your location');
      });
    }
  }

  const stopLocation = () => {
    watchId = getLocation();
      navigator.geolocation.clearWatch(watchId);
      console.log("location stopped");
    }

  return (
    <div className="Location">
      <button onClick={getLocation}>Start Journey</button>
      <button onClick={stopLocation}>Stop Journey</button>
    </div>
  );
}
export default Location;
