import React from 'react'

import './home.css'
import {Icon} from "@iconify/react/dist/iconify";
import locationIcon from "@iconify/icons-mdi/map-marker";

import GoogleMapReact from "google-map-react";
import MapPopup from "../dialog/LocationDialog";
import LocationDialog from "../dialog/LocationDialog";
import Footer from "../footer/Footer";
import Offcanvas from 'react';
import Button from 'react';
import useState from 'react';
import render from 'react';



const LocationPin = ({ text }) => (
    <div className="pin">
        <Icon icon={locationIcon} className="pin-icon" />
        <p className="pin-text">{text}</p>
    </div>
)

this.state = {
 lat: 1.3521,
 lng: 103.8198,
 zoom: 11,
 addLat: 1.3521,
 addLng: 103.8198,
 data:[
   {
    "latitude":"1.3589783333",
    "is_on_trip":false,
    "id":5,
    "longitude":"103.752293000"
   },
   {
    "latitude":"1.3492333333",
    "is_on_trip":true,
    "id":6,
    "longitude":"103.729582"
   },
   {
    "latitude":"1.2894376666",
    "is_on_trip":true,
    "id":8,
    "longitude":"103.812434499"
   },
 ]
}


class Home extends React.Component{
    constructor(props) {
        super(props);
        this.state = {locations: [{
                address: 'Initial Pin',
                lat: 52.49632997574632,
                lng: 13.437847871710412,
            }]};
    }
    renderPolylines (map, maps) {
       /** Example of rendering geodesic polyline */
       let geodesicPolyline = new maps.Polyline({
         path: this.props.markers,
         geodesic: true,
         strokeColor: '#00a1e1',
         strokeOpacity: 1.0,
         strokeWeight: 4
       })
       geodesicPolyline.setMap(map)

       /** Example of rendering non geodesic polyline (straight line) */
       let nonGeodesicPolyline = new maps.Polyline({
         path: this.props.markers,
         geodesic: false,
         strokeColor: '#e4e4e4',
         strokeOpacity: 0.7,
         strokeWeight: 3
       })
       nonGeodesicPolyline.setMap(map)

       this.fitBounds(map, maps)
     }

     fitBounds (map, maps) {
       var bounds = new maps.LatLngBounds()
       for (let marker of this.props.markers) {
         bounds.extend(
           new maps.LatLng(marker.lat, marker.lng)
         )
       }
       map.fitBounds(bounds)
     }
    render(){
      const Markers = this.state.data.map(d => (
        <Marker
          key = {d.id}
          lat = {d.latitude}
          lng = {d.longitude}
          text="my marker"
          fontColor="red"
        />
      ))
        return (
            <div className="map">
                <div className="google-map">
                    <GoogleMapReact
                        onClick={e => {
                            console.log('New Pin');
                            console.log(e);
                            this.setState({
                                locations: [{
                                    address: 'New Pin',
                                    lat: e.lat,
                                    lng: e.lng,
                                }]
                            });
                            return <MapPopup/>;
                        } }
                        bootstrapURLKeys={{ key:'AIzaSyBlh6UoKeNh9fXnUkgtUVfVpZEbopyFmXE'}}
                        defaultCenter={{lat, lng}}
                        center={[this.state.addLat, this.state.addLng]}
                        defaultZoom={zoom}>
                        {this.state.locations.map(function(object, i){
                            return <LocationPin
                                key={i}
                                lat={object.lat}
                                lng={object.lng}
                                text={object.address}
                            />;
                        })}
                        onGoogleApiLoaded={({map, maps}) => this.renderPolylines(map, maps)}>
                        <Marker text={'DUB'} lat={53.42728} lng={-6.24357} />
                        <Marker text={'YYZ'} lat={43.681583} lng={-79.61146} />
                    </GoogleMapReact>

                </div>

    <Footer/>
            </div>

        );
  }
}

export default Home;
